import React from "react";
import { CardItemType } from "../content/types";
import i18next from "i18next";

const FlipCard = (item: CardItemType) => {
  const language = i18next.language;
  // make cardItems array of objects to iterate over
  // each object should have title, eng content, ser content, icon properties
  // content and title change based on the language
  return (
    <>
      <p>{language === "en" ? item.engTitle : item.serTitle}</p>
      <p>{item.icon}</p>
      <p>{language === "en" ? item.engContent : item.serContent}</p>
    </>
  );
};

export default FlipCard;
