import React from "react";
import { CardItemType } from "../content/types";
import i18next from "i18next";

const FlipCard = (item: CardItemType) => {
  const language = i18next.language;
  const { engTitle, serTitle, engContent, serContent, icon: Icon } = item;
  // const language = i18next.language;
  // make cardItems array of objects to iterate over
  // each object should have title, eng content, ser content, icon properties
  // content and title change based on the language
  return (
    <div className="relative h-[300px] w-[300px] rounded-xl shadow-xl transition-all duration-500 [transform-style:preserve-3d] hover:[tranform">
      <div className="absolute inset-0">
        <p>{language === "en" ? engTitle : serTitle}</p>
        <Icon />
      </div>
      <div className="absolute inset-0 h-[300px] w-[300px] rounded-xl bg-teal-500 p-4">
        <p>{language === "en" ? engTitle : serTitle}</p>
        <Icon />
        <p>{language === "en" ? engContent : serContent}</p>
      </div>
    </div>
  );
};

export default FlipCard;
